bj.PrizeId;                    Errorid = jsonobj.ErrorId;                    Rotate.isFaild = false;                    Rotate.isStop = true;                };                request.onFailure = function() {                    Rotate.isFaild = true;                    Rotate.isStop = true;                };                request.onException = function() {                    Rotate.isFaild = true;                    Rotate.isStop = true;                };                request.send(null);            };            Rotate.rotateY = function(id, _deg) {                var element = $(id);                element.style.MozTransform = 'rotateZ(' + _deg + 'deg)';                element.style.WebkitTransform = 'rotateZ(' + _deg + 'deg)';                element.style.OTransform = 'rotateZ(' + _deg + 'deg)';                element.style.MsTransform = 'rotateZ(' + _deg + 'deg)';                element.style.Transform = 'rotateZ(' + _deg + 'deg)';            };            $('inner').onclick = function() {                if (Rotate.isRun) {                    return;                }                Rotate.run();            };            function alert_t(msg) {                $('alertEleMsg').innerHTML = msg;                $('alertEle').style.display = 'block';            }            function alert_tt(msg)            {                $('alertElleMsg').innerHTML = msg;                $('alertElle').style.display = 'block';            }            function down()            {                window.location.reload();            }        </script>            </body></html><?php }} ?>